import React from 'react'
import { useContext } from 'react'
import { useState } from 'react'
import { globalContext } from '../store'
import { loginType, serverResponse } from '../types'

const Login: React.FunctionComponent<any> = () => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [errorMessage, setErrorMessage] = useState('username or password error!')

  const {state, dispatch} = useContext(globalContext)

  // reusable input change
  const handleInputChange = (event: React.ChangeEvent<HTMLElement>): void => {
    try {

        // get HTML element name and value from event target
        const { name, value }: any = event.target
        set+[name]
        // update application state
        dispatch({ [name]:value, isDisabled:false })

    } catch (error) {
        dispatch({ "notification": (error as Error).message })
    }
  }



  // validation
  let errors = []
  const inputs_body: loginType = { username: username, password: password }
  function validateInputs(inputs_body: loginType) {
    if (inputs_body.username === '' || inputs_body.password === '') {
      errors.push('')
    }
  }

  // submission
async function submitForm(e: any){
    e.preventDefault()
    validateInputs(inputs_body)

    if (errors.length > 0) {
      dispatch({ isError:true })
      dispatch({ isDisabled:true })
      return
    }

    dispatch({ isLoading:true })
    const headers   = new Headers({
          'Content-Type': 'application/json',
          'Authorization': 'Bear '+ state.token
    })
    const data: serverResponse = await (await fetch('http://localhost:8080/endpoints/auth/user',
        {
          mode:'cors',
          method: "POST",
          body: JSON.stringify(inputs_body),
          headers: headers
        },
      )).json()


    console.log(data)

    if ((data && !data.success) && data.message) {
      dispatch({  })

      dispatch({  })

      setErrorMessage(data.message)
      return
    }

    if ((data && !data.success) && data.message === '') {
      dispatch({  })

      dispatch({  })

      return
    }

    if (data) {
      dispatch({  })
      // store token into local storage
      // redirect to dashboard
    }
  }








  return (
    <React.Fragment>
      <div className='row'>
        <div className='col-md-4 offset-md-4'>

          <div className='card mt-5'>
            <div className='mx-auto mt-3'>
              <img
                className='rounded-pill'
                src="https://cdn.pixabay.com/photo/2013/10/09/02/27/lake-192990__340.jpg" alt="" width={150} height={150}
              />
            </div>
            <div className='card-body'>
              <div className="card-title text-muted text-center text-uppercase">Login Form</div>
              <form onSubmit={submitForm}>
                <div className='mb-3'>
                  <input
                    className='form-control'
                    type="text" name='username'
                    placeholder='username or phone'
                    value={username}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="password"
                    className="form-control"
                    name='password' placeholder='password'
                    value={password}
                    onChange={handleInputChange}
                  />
                </div>
                <div className='mb-3'>
                  {state.isLoading ?
                    (
                      <div className="d-flex justify-content-center">
                        <div className="spinner-border" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </div>
                      </div>
                    ) : (<button className='btn btn-primary w-100 text-uppercase' disabled={state.isDisabled}>login</button>)}
                </div>
                {state.isError ? (<p className='text-center bg-danger p-2 text-light rounded'>{errorMessage}</p>) : ''}

              </form>
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}

export default Login
