import React from 'react'
import { useContext } from 'react'
import { useState } from 'react'
import { globalContext } from '../store'
import { loginType, serverResponse } from '../types'
import { Button } from '@blueprintjs/core/lib/esm/components/button/buttons'

const Login: React.FunctionComponent<any> = () => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [errorMessage, setErrorMessage] = useState('username or password error!')

  const { state, dispatch } = useContext(globalContext)

  // reusable input change
  const handleInputChange = (event: React.ChangeEvent<HTMLElement>): void => {
    try {
      // get HTML element name and value from event target
      const { name, value }: any = event.target
      if (name === 'username') {
        setUsername(value)
      }

      if (name === 'password') {
        setPassword(value)
      }
      // update application state
      // dispatch({ [name]:value, isDisabled:false })
      dispatch({ isDisabled: false })
    } catch (error) {
      dispatch({ "notification": (error as Error).message })
    }
  }



  // validation
  let errors = []
  const inputs_body: loginType = { username: username, password: password }
  function validateInputs(inputs_body: loginType) {
    if (inputs_body.username === '' || inputs_body.password === '') {
      errors.push('')
    }
  }

  // submission
  async function submitForm(e: any) {
    e.preventDefault()
    validateInputs(inputs_body)

    if (errors.length > 0) {
      dispatch({ isError: true })
      dispatch({ isDisabled: true })
      return
    }

    dispatch({ isLoading: true })
    const headers = new Headers({
      'Content-Type': 'application/json',
      'Authorization': 'Bear ' + state.token
    })
    const data: serverResponse = await (await fetch('http://localhost:8080/endpoints/auth/user',
      {
        mode: 'cors',
        method: "POST",
        body: JSON.stringify(inputs_body),
        headers: headers
      },
    )).json()


    console.log(data)

    if ((data && !data.success) && data.message) {
      dispatch({})

      dispatch({})

      setErrorMessage(data.message)
      return
    }

    if ((data && !data.success) && data.message === '') {
      dispatch({})

      dispatch({})

      return
    }

    if (data) {
      dispatch({})
      // store token into local storage
      // redirect to dashboard
    }
  }








  return (
    <React.Fragment>
      <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
        <div className="relative py-3 sm:max-w-xl sm:mx-auto">
          <div
            className="absolute inset-0 bg-gradient-to-r from-blue-300 to-blue-600 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl">
          </div>
          <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
            <div className="max-w-md mx-auto">
              <div>
                <h1 className="text-2xl font-semibold text-center">Login Form | Welcome</h1>
              </div>
              <div className="divide-y divide-gray-200">
                <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                  <div className="relative">
                    <input autoComplete="off" id="email" name="email" type="text" className="peer placeholder-transparent h-10 w-full border-b-2 border-gray-300 text-gray-900 focus:outline-none focus:borer-rose-600 px-2" placeholder="Email address" />
                  </div>
                  <div className="relative">
                    <input 
                      autoComplete="off" 
                      id="password" 
                      name="password" 
                      type="password" 
                      on
                      className="peer placeholder-transparent h-10 w-full border-b-2 border-gray-300 text-gray-900 focus:outline-none focus:borer-rose-600 px-2" placeholder="Password" />
                  </div>
                  <div className="relative">
                    <Button intent='primary' className="rounded-md px-2 py-1 w-full uppercase " icon='lock'  text="login"></Button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}

export default Login
