import React from 'react'
import { useContext } from 'react'
import { useState } from 'react'
import { globalContext } from '../store'
import { loginType, serverResponse } from '../types'
import { Button } from '@blueprintjs/core/lib/esm/components/button/buttons'

const Login: React.FunctionComponent<any> = () => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [errorMessage, setErrorMessage] = useState('username or password error!')

  const {state, dispatch} = useContext(globalContext)

  // reusable input change
  const handleInputChange = (event: React.ChangeEvent<HTMLElement>): void => {
    try {
        // get HTML element name and value from event target
        const { name, value }: any = event.target
        if(name === 'username'){
          setUsername(value)
        }

        if(name === 'password'){
          setPassword(value)
        }
        // update application state
        // dispatch({ [name]:value, isDisabled:false })
        dispatch({ isDisabled:false })
    } catch (error) {
        dispatch({ "notification": (error as Error).message })
    }
  }



  // validation
  let errors = []
  const inputs_body: loginType = { username: username, password: password }
  function validateInputs(inputs_body: loginType) {
    if (inputs_body.username === '' || inputs_body.password === '') {
      errors.push('')
    }
  }

  // submission
async function submitForm(e: any){
    e.preventDefault()
    validateInputs(inputs_body)

    if (errors.length > 0) {
      dispatch({ isError:true })
      dispatch({ isDisabled:true })
      return
    }

    dispatch({ isLoading:true })
    const headers   = new Headers({
          'Content-Type': 'application/json',
          'Authorization': 'Bear '+ state.token
    })
    const data: serverResponse = await (await fetch('http://localhost:8080/endpoints/auth/user',
        {
          mode:'cors',
          method: "POST",
          body: JSON.stringify(inputs_body),
          headers: headers
        },
      )).json()


    console.log(data)

    if ((data && !data.success) && data.message) {
      dispatch({  })

      dispatch({  })

      setErrorMessage(data.message)
      return
    }

    if ((data && !data.success) && data.message === '') {
      dispatch({  })

      dispatch({  })

      return
    }

    if (data) {
      dispatch({  })
      // store token into local storage
      // redirect to dashboard
    }
  }








  return (
    <React.Fragment>
      <div class="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
	<div class="relative py-3 sm:max-w-xl sm:mx-auto">
		<div
			class="absolute inset-0 bg-gradient-to-r from-blue-300 to-blue-600 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl">
		</div>
		<div class="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
			<div class="max-w-md mx-auto">
				<div>
					<h1 class="text-2xl font-semibold">Login Form with Floating Labels</h1>
				</div>
				<div class="divide-y divide-gray-200">
					<div class="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
						<div class="relative">
							<input autocomplete="off" id="email" name="email" type="text" class="peer placeholder-transparent h-10 w-full border-b-2 border-gray-300 text-gray-900 focus:outline-none focus:borer-rose-600" placeholder="Email address" />
							<label for="email" class="absolute left-0 -top-3.5 text-gray-600 text-sm peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-440 peer-placeholder-shown:top-2 transition-all peer-focus:-top-3.5 peer-focus:text-gray-600 peer-focus:text-sm">Email Address</label>
						</div>
						<div class="relative">
							<input autocomplete="off" id="password" name="password" type="password" class="peer placeholder-transparent h-10 w-full border-b-2 border-gray-300 text-gray-900 focus:outline-none focus:borer-rose-600" placeholder="Password" />
							<label for="password" class="absolute left-0 -top-3.5 text-gray-600 text-sm peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-440 peer-placeholder-shown:top-2 transition-all peer-focus:-top-3.5 peer-focus:text-gray-600 peer-focus:text-sm">Password</label>
						</div>
						<div class="relative">
							<button class="bg-blue-500 text-white rounded-md px-2 py-1">Submit</button>
						</div>
					</div>
				</div>
			</div>
		</div>
	</div>
</div>
    </React.Fragment>
  )
}

export default Login
